sourcesContent:
import "dotenv/config";
import express from "express";
import cors from "cors";
import compress from "compression";
import cookieParser from "cookie-parser";
import helmet from "helmet";
import models, { sequelize } from "./models/init-models";
import routes from "./Routes/indexRoutes";

const port = process.env.PORT || 3000;
const app = express();

app.use(express.json());
app.use(express.urlencoded({ extended: true }));
app.use(cookieParser());
app.use(helmet());
app.use(compress());
app.use(cors());
app.use(async (req, res, next) => {
  req.context = { models };
  next();
});

app.use("/region", routes.RegRoute);
app.use("/country", routes.CountryRoute);
app.use("/location", routes.LocationRoute);
app.use("/department", routes.DepartmentRoute);
app.use("/job", routes.JobRoute);
app.use("/dependent", routes.DependentRoute);
app.use("/employee", routes.EmployeeRoute);
app.use("/project", routes.ProjectRoute);
app.use("/project_assignment", routes.project_assignmentRoute);

const dropDatabaseSync = false;
sequelize.sync({ force: dropDatabaseSync }).then(async () => {
  if (dropDatabaseSync) {
    console.log("Database do not drop");
  }
  app.listen(port, () => {
    console.log("Server is listening on port " + port);
  });
});

export default app;

